mapper.update:
update tbl_customer
set customerId=#{customerId}, pwd= #{pwd}, showName=#{showName}, trueName=#{trueName}, registerTime=#{registerTime}
where uuid = #{uuid}

mapper.select:
select uuid, customerId, pwd, showName, trueName, registerTime
from tbl_customer 
where uuid = #{uuid}

select uuid, customerId, pwd, showName, trueName, registerTime
from tbl_customer


        and uuid = #{uuid}


        and customerId = #{customerId}


        and showName = #{showName}

select uuid, customerId, pwd, showName, trueName, registerTime
from tbl_customer
where customerId = #{customerId}
